#include "../utility.h"

#include <fstream>
#include <iostream>
#include <vector>
#include <numeric>
#include <algorithm>
#include <bitset>




constexpr int FindRepeats(const std::string& message) {
  assert(message.size() > 4);
  int idx = 1;
  constexpr int marker_size = 14;

  while(idx < message.size()) {
    bool found = true;

    for(int s1=0; s1<marker_size; ++s1){
      // Compare m[idx-s1] with previous chars
      for(int s2 = s1+1; found and (s2 < std::min(idx+1,marker_size)); ++s2) {
        if(message[idx-s1] == message[idx-s2]) {

          idx += marker_size - s2;
          found = false;
        }
      }
    }
    
    if(idx < marker_size) { ++idx; }
    else if(found) { return idx+1; }
  }

  return -1;
}

int main() {

  constexpr const char* message = "";

  //static_assert(FindRepeats(message) == 2635);

  std::cout << FindRepeats(message);

  return 0;
}
